import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useRef, useContext, useState } from 'react';
import { StyleSheet, Text, View, SafeAreaView, TouchableOpacity, ScrollView, Button, Alert} from 'react-native';
import StarRating from 'react-svg-star-rating'
import Product from "./models/product";
import Review from "./models/review"
import Items from './components/Items';
import { RootStoreContext } from "./stores/root-store";
import { observer, useAsObservableSource } from "mobx-react-lite"
import { Dimensions } from "react-native";

var width = Dimensions.get('window').width; //full width
var height = Dimensions.get('window').height; //full height
const width_Content = '80%';
const height_Content = '75%';
const width_Review = '80%';

const App = (() => {

  return (
    <SafeAreaView style={styles.container}>
		<View style={styles.modalHeader}>
			<Button title ="closeButton" data-cy="close-button" onPress={() => Alert.alert("closing")}>
				<Text>&#10006;</Text>
			</Button>
		</View>
      	<Text>Hello world </Text>
      	<StatusBar style="auto" />
    </SafeAreaView>
  );
});

const styles = StyleSheet.create({
    container: {
		flex: 1,
		backgroundColor: '#fff',
		//alignItems: 'center',
		//justifyContent: 'center'
	},
	modalContainer : {
		backgroundColor: 'rgba(0, 0, 0, 0.95)',
		top: 0,
		left: 0,
		width: width,
		height: height,
		flex:1,
		alignItems: 'center',
		justifyContent: 'center',
		opacity:0

	},
	modalContent: {
		flex: 1,
		flexDirection: 'column',
		backgroundColor: 'rgb(255, 255, 255)',
		width: width_Content,
		height: height_Content
	},
	modalHeader: {
		fontSize: 10,
	},
	closeButton: {
		padding: 10,
		top: 10,
		right: 10,
		zIndex: 10,
		color:'#FFFFFF',
		justifyContent: 'flex-end',
		position:'absolute',
	},
	sendButton: {
		padding:'10px',
	},
	reviews: {
		width: width_Review,
		padding: '0 10px 10px 10px',
		zIndex: 10
	},
	review: {
		padding: '10px',
	},
	reviewInput: {
		//Fikse grid
		flexDirection: 'row',
		justifyContent: 'space-between'
		//display: grid;
		//grid-template-columns: 50% 50%;
		//grid-gap: 0%;
	},
	reviewUser: {
		width: '30%',
	},
	reviewComment: {
		width: '60%',
	}
  })

  export default App;
